import React, { Component } from "react";

class FormAndTable extends Component {
  constructor() {
    super();
    this.state = {
      inputData: "",
      tableData: [],
    };
  }

  handleInputChange = (e) => {
    this.setState({
      inputData: e.target.value,
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const { inputData, tableData } = this.state;

    if (inputData.trim() !== "") {
      // Add the input data to the tableData array
      this.setState({
        tableData: [...tableData, inputData],
        inputData: "", // Clear the input field
      });
    }
  };

  render() {
    const { inputData, tableData } = this.state;

    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <input
            type="text"
            id="extra-details"
            value={inputData}
            onChange={this.handleInputChange}
            placeholder="Enter data"
          />
          <button type="submit">Add to Table</button>
        </form>
        <table>
          <thead>
            <tr>
              <th>Data</th>
            </tr>
          </thead>
          <tbody>
            {tableData.map((data, index) => (
              <tr key={index}>
                <td>{data}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default FormAndTable;
