import React, { useState } from "react";
import Button from "./button";
import Form from "./Form.js";
import Table from "./Table.js";

const UpdateForm = ({ tableData, setTableData }) => {
  const [formData, setFormData] = useState({
    customer_type: "",
    reff_no: "",
    title: "",
    company_name: "",
    customer_name: "",
    identity_number: "",
    open_balance: "",
    sales_person: "",
    posting_address: "",
    extra_details: [],
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = () => {
    // Validate form data if needed

    // Add form data to extra_details array
    const updatedFormData = { ...formData };
    updatedFormData.extra_details = [
      ...formData.extra_details,
      {
        customer_name: formData.customer_name,
        designation: formData.designation,
        mobile: formData.mobile,
        email: formData.email,
        address: formData.address,
      },
    ];

    // Update tableData with the new data
    setTableData([...tableData, updatedFormData]);

    // Clear form fields
    setFormData({
      customer_type: "",
      reff_no: "",
      title: "",
      company_name: "",
      customer_name: "",
      identity_number: "",
      open_balance: "",
      sales_person: "",
      posting_address: "",
      extra_details: [],
    });
  };

  const handleDeleteRow = (index) => {
    const updatedTableData = [...tableData];
    updatedTableData.splice(index, 1);
    setTableData(updatedTableData);
  };

  return (
    <Form title="Update Employee">
      {/* ... Your form elements ... */}
      <div className="form-group">
        {/* ... Your form inputs ... */}
        <Button
          type="button"
          name="+"
          onClick={handleSubmit}
          className="w-10 h-10 rounded-full bg-blue-500 hover:bg-red-500 text-white mt-5"
        />
      </div>
      {/* ... Your table */}
      <Table data={tableData} handleDeleteRow={handleDeleteRow} />
      <Button
        type="button"
        name="Update"
        className="inline-block rounded bg-primary px-6 pb-2 pt-2.5 text-xs font-medium uppercase leading-normal text-white shadow-[0_4px_9px_-4px_#3b71ca] transition duration-150 ease-in-out hover:bg-primary-600 hover:shadow-[0_8px_9px_-4px_rgba(59,113,202,0.3),0_4px_18px_0_rgba(59,113,202,0.2)] focus:bg-primary-600 focus:shadow-[0_8px_9px_-4px_rgba(59,113,202,0.3),0_4px_18px_0_rgba(59,113,202,0.2)] focus:outline-none focus:ring-0 active:bg-primary-700 active:shadow-[0_8px_9px_-4px_rgba(59,113,202,0.3),0_4px_18px_0_rgba(59,113,202,0.2)] dark:shadow-[0_4px_9px_-4px_rgba(59,113,202,0.5)] dark:hover:shadow-[0_8px_9px_-4px_rgba(59,113,202,0.2),0_4px_18px_0_rgba(59,113,202,0.1)] dark:focus:shadow-[0_8px_9px_-4px_rgba(59,113,202,0.2),0_4px_18px_0_rgba(59,113,202,0.1)] dark:active:shadow-[0_8px_9px_-4px_rgba(59,113,202,0.2),0_4px_18px_0_rgba(59,113,202,0.1)]"
      />
    </Form>
  );
};

export default UpdateForm;
