import React, { useState } from "react";
import { UpdateForm } from "./UpdateForm";
import Table from "./Table";

function ParentComponent() {
  const [tableData, setTableData] = useState([]); // Define and initialize tableData

  // Function to add data to the tableData state
  const addDataToTable = (formData) => {
    // Update the tableData state with the new data
    setTableData([...tableData, formData]);
  };

  return (
    <div>
      <UpdateForm addDataToTable={addDataToTable} tableData={tableData} />
      <Table data={tableData} /> {/* Render the Table component */}
    </div>
  );
}

export default ParentComponent;
