import React, { useState } from "react";
import Button from "./button";
import Form from "./Form.js";
import Table from "./Table.js";

export const UpdateForm = ({ addDataToTable, tableData }) => {
  const [formData, setFormData] = useState({
    customer_name: "",
    email: "",
    // Add more fields here as needed
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleExtraDetailsFormSubmit = (e) => {
    e.preventDefault(); // Prevent the default form submission
    // Add the form data to the tableData array
    addDataToTable(formData);
    // Clear the form fields
    setFormData({
      customer_name: "",
      email: "",
      // Clear other fields as needed
    });
  };

  return (
    <Form title="Update Employee">
      {/* ... (your existing form elements) */}
      <form id="extra-details-form" onSubmit={handleExtraDetailsFormSubmit}>
        {/* Your additional form fields */}
        {/* For example: */}
        <div className="form-group">
          <label
            htmlFor="designation"
            className="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
          >
            Designation
          </label>
          <input
            type="text"
            id="designation"
            name="designation"
            value={formData.designation}
            onChange={handleInputChange}
            // Add other attributes and placeholders as needed
          />
        </div>
        {/* ... Add more fields here as needed */}
        <Button
          type="submit"
          name="+"
          className="w-10 h-10 rounded-full bg-blue-500 hover:bg-red-500 text-white mt-5"
        />
      </form>
      {/* ... (your table and other components) */}
    </Form>
  );
};
