// src/components/Table.js
import React from "react";

const Table = ({ data, handleMainInputChange }) => {
  const [editMode, setEditMode] = useState([]);

  // Function to toggle edit mode for a specific row
  const toggleEditMode = (index) => {
    const updatedEditMode = [...editMode];
    updatedEditMode[index] = !editMode[index];
    setEditMode(updatedEditMode);
  };

  return (
    <div className="grid gap-6 mb-6 md:grid-cols-1 mt-2">
      <div className="relative overflow-x-auto shadow-md sm:rounded-lg">
        <table
          className="w-full text-sm text-left text-gray-500 dark:text-gray-400"
          id="comman_table"
        >
          <thead className="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
            <tr>
              <th scope="col" className="px-6 py-3">
                Name
              </th>
              <th scope="col" className="px-6 py-3">
                Designation
              </th>
              <th scope="col" className="px-6 py-3">
                Mobile
              </th>
              <th scope="col" className="px-6 py-3">
                Email
              </th>
              <th scope="col" className="px-6 py-3">
                Address
              </th>
              <th scope="col" className="px-6 py-3">
                Action
              </th>
            </tr>
          </thead>
          <tbody>
            {data == "" && (
              <tr>
                <td colSpan={6} className="text-center p-5">
                  <b> No Data Found </b>
                </td>
              </tr>
            )}
            {data != "" &&
              data.map((item, index) => (
                <tr
                  key={index}
                  className="bg-white border-b dark:bg-gray-900 dark:border-gray-700"
                >
                  <th
                    scope="row"
                    className="px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white"
                  >
                    <input
                      type="text"
                      value={item.name}
                      name={`name[${index}]`}
                      onChange={handleMainInputChange}
                      readOnly
                    ></input>
                    {editMode[index] ? (
                      <input
                        type="text"
                        value={item.name}
                        onChange={(e) => handleInputChange(e, index)}
                      />
                    ) : (
                      item.name
                    )}
                  </th>
                  <td className="px-6 py-4">
                    <input
                      type="text"
                      value={item.designation}
                      name={`designation[${index}]`}
                      onChange={handleMainInputChange}
                    ></input>
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.mobile_no}
                      name={`mobile_no[${index}]`}
                      onChange={handleMainInputChange}
                    ></input>
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.email}
                      onChange={handleMainInputChange}
                      name={`email[${index}]`}
                    ></input>
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.address}
                      onChange={handleMainInputChange}
                      name={`address[${index}]`}
                    ></input>
                  </td>
                  <td className="px-6 py-4">
                    {/* Toggle edit mode button */}
                    <button onClick={() => toggleEditMode(index)}>
                      {editMode[index] ? "Save" : "Edit"}
                    </button>
                  </td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Table;
